INSERT INTO `wp_posts` VALUES (9, 2, '2008-06-16 13:13:33', '2008-06-16 06:13:33', '<span class="gen">Cuối cùng rồi thì mình cũng chia tay\r\nRồi thì mình cũng xa nhau\r\nTình yêu ấy giờ phai màu\r\nNhìn nhau giây phút cuối đứng yên chẳng nói gì\r\nRồi hai ta quay lưng lìa xa</span>\r\n\r\nCuối cùng rồi thì người cũng yên vui\r\nVì giờ người có ai kia\r\nTình yêu mới thật êm đềm\r\nNhìn em hạnh phúc suốt tháng năm chẳng biết buồn\r\nLòng thầm vui em đã bình yên...\r\n\r\n<!--more-->Có những đêm mình anh ngồi nhìn ánh sao\r\nNước mắt âm thầm rơi cứ khóc khi nhớ em\r\nChỉ ước mong đời em thật sự ấm nồng\r\nDẫu người vẫn mơ\r\n<span style="font-size: 1px;">\r\n</span>\r\n\r\nĐK:\r\nNiềm hạnh phúc anh có trong cõi đời\r\nLà trông thấy, thấy em được hạnh phúc\r\nDù cho hai ta xa nhau hay em chẳng yêu anh nữa\r\nLòng anh vẫn hướng về em\r\n\r\nNiềm hạnh phúc em có hay biết rằng\r\nNgày ấy sẽ mãi không thể nào có\r\nVì sao trong con tim anh yêu em chẳng thay đổi\r\nDù anh có bước cùng ai\r\nVẫn yêu người\r\n\r\n***\r\nNgười biết không\r\nThấy em vui anh cũng vui lòng\r\nVì khi yêu ai cũng mong người mình yêu suốt cuộc đời\r\nĐược hạnh phúc mà thôi...\r\n\r\n<p style="text-align: center;"><object classid="clsid:6bf52a52-394a-11d3-b153-00c04f79faa6" width="100" height="100" codebase="http://activex.microsoft.com/activex/controls/mplayer/en/nsmp2inf.cab#Version=5,1,52,701"><param name="autostart" value="true" /><param name="url" value="http://s1.vui360.com/blog/music/Co Thu Hanh Phuc Goi La Chia Tay - Huyen Thoai (ChiaSeNhac.com).wma" /><embed type="application/x-mplayer2" width="100" height="100" autostart="true" url="http://s1.vui360.com/blog/music/Co Thu Hanh Phuc Goi La Chia Tay - Huyen Thoai (ChiaSeNhac.com).wma.txt"></embed></object>', 'Có thứ hạnh phúc gọi là chia tay', 0, '', 'publish', 'open', 'open', '', 'co-thu-hanh-phuc-goi-la-chia-tay', '', '', '2008-06-22 11:31:36', '2008-06-22 04:31:36', '', 0, 'http://vui360.com/blog/?p=9', 0, 'post', '', 0);
